    private void btnSalvarVeiculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalvarVeiculoActionPerformed
        String marca = txtMarca.getText();
        String modelo = txtModelo.getText();
        String ano = txtAno.getText();
        String placa = txtPlaca.getText();

        if (marca.isEmpty() || modelo.isEmpty() || ano.isEmpty() || placa.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Preencha todos os campos!", "Erro", JOptionPane.ERROR_MESSAGE);
        } else {
            Veiculo veiculo = new Veiculo(marca, modelo, ano, placa);
            listaVeiculos.add(veiculo); // ðŸ‘ˆ Salvando na lista

            JOptionPane.showMessageDialog(this, "VeÃ­culo salvo com sucesso!");

            txtMarca.setText("");
            txtModelo.setText("");
            txtAno.setText("");
            txtPlaca.setText("");
        }
    }//GEN-LAST:event_btnSalvarVeiculoActionPerformed

